/* eslint-disable jsx-a11y/label-has-associated-control */

import React, { Component } from 'react'
import './Profile1Phone.css'
import './ProfilesDesktop.css'
import GetProfile from './GetProfile'

class WelcomePage extends Component {
  constructor() {
    super()
    this.state = {
      username: null,
      location: null,
      avatar_url: null,
      public_repos: null,
      isCardIsVisible: false,
      repos: null,
    }
  }

  getUser(username) {
    return fetch(`https://api.github.com/users/${username}`)
      .then(response => response.json())
      .then(response => response)
  }

  getUserLanguage(username) {
    const languagesIn = repos => repos.reduce((acc, repo) => (acc.filter(repoInAcc => repoInAcc.language === repo.language).length
      ? acc.map(repoInAcc => (repoInAcc.language === repo.language
        ? { ...repoInAcc, count: repoInAcc.count + 1 }
        : repoInAcc))
      : [
        ...acc,
        {
          language: repo.language,
          count: 1,
        },
      ]), [])

    const languages = languagesIn(repos)

    const maxIn = languages => {
      let counter = 0
      let winnerLanguage = ''
      languages.map(language => {
        if (language.count >= counter) {
          counter = language.count
          winnerLanguage = language.language
        }
      })
      return winnerLanguage
    }

    return fetch(`https://api.github.com/users/${username}/repos`)
      .then(response => response.json())
      .then(response => { this.languagesIn(response) })
      .then(response => { this.maxIn(response) })
  }

  // componentDidMount() {
