  //       },
  //     ]), [])

  //   const languages = languagesIn(repos)

  //   const maxIn = languages => {
  //     let counter = 0
  //     let winnerLanguage = ''
  //     languages.map(language => {
  //       if (language.count >= counter) {
  //         counter = language.count
  //         winnerLanguage = language.language
  //       }
  //     })
  //     return winnerLanguage
  //   }

  //   return fetch(`https://api.github.com/users/${username}/repos`)
  //     .then(response => response.json())
  //     .then(response => { this.languagesIn(response) })
  //     .then(response => { this.maxIn(response) })
  // }

  cardAppear() {
    this.setState({ isCardIsVisible: true })
  }

  async handleSubmit(e) {
    e.preventDefault()
    const user = await this.getUser(this.refs.username.value)
    this.setState({
      username: user.login,
      avatar_url: user.avatar_url,
      location: user.location,
      public_repos: user.public_repos,
    })
    this.cardAppear()
  }

  async buttonSubmit(e) {
    e.preventDefault()
    const user = await this.getUser(this.refs.username.value)
    this.setState({
      username: user.login,
      avatar_url: user.avatar_url,
      location: user.location,
      public_repos: user.public_repos,
    })
    this.cardAppear()
  }

  render() {
    return (
      <div>
        <div id="WelcomePage">
          <h1>Welcome to Git Versus</h1>
          <form onSubmit={e => this.handleSubmit(e)}>
            <input ref="username" type="text" placeholder="username" />
            <button type="button" onClick={e => this.buttonSubmit(e)}>Search</button>
          </form>
          <div id={this.state.isCardIsVisible ? 'ProfileOn' : 'ProfileOff'}>
            <GetProfile
              username={this.state.username}
              avatar_url={this.state.avatar_url}
              public_repos={this.state.public_repos}
              location={this.state.location}
              weapons="JavaScript"
            />
          </div>
        </div>
      </div>
    )
  }
}

export default WelcomePage
